import React from 'react'
import axios from 'axios';
import { Container } from 'react-bootstrap';
import './Logincontainer.css'
import { Redirect } from 'react-router';


export default class Logincontainer extends React.Component{
    constructor(props){
        super(props);
        this.state={
            email: " ",
            password: " "
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this)
    }
      
      handleChange = (event) => {
      this.setState( { [event.target.name]: event.target.value } )
    }
    handleSubmit(event) {
        const { email,password } = this.state;
   
    
   
   axios.post(`http://localhost:8081/api/users/login`,null,{ params: { userEmail: email, userPassword: password }})
   .then(response => {
    if (response.data.logged_in) {
      this.props.handleSuccessfulAuth(response.data).then(
        () => {
          return <Redirect to="/profile" />
        }) 
      
     
    }
  }) 
  .catch(error => {
    console.log("login error", error);
    alert("this is not correct")
    
  });
  
        event.preventDefault();
   }
//    
   render() {
       
    return (
        <Container className="signin-box">
      <div>
        <form onSubmit={this.handleSubmit}>
          <input className="login"
            type="email"
            name="email"
            placeholder="email"
            value={this.state.email}
            onChange={this.handleChange}
            required
          />

          <input className="login"
            type="password"
            name="password"
            placeholder="Password"
            value={this.state.password}
            onChange={this.handleChange}
            required
          />

          <button type="submit" >Login</button>
        </form>
      </div>
      </Container>
    );
  }
       

        
   
}



