import React from 'react';
import '../Components/nav.css';
import Nav from '../Components/nav.js';
import food4 from '../Resources/food5.jpg';
import axios from 'axios';
class Signupcontainer extends React.Component {
    constructor (props){
      super(props);

      this.state = {
        name: '',
        email: '',
        password: ''
      }
      this.handleChange
    }
    
  
    handleChange = (event) => {
      this.setState( { [event.target.id]: event.target.value } )
    }
  
    handleSubmit = event => {
      event.preventDefault();
  
      const user = {
        name: this.state.name,
        email: this.state.email,
        password: this.state.password,
      };

      console.log(user);
     
      axios.post(`http://localhost:8081/api/users/new-account`, user)
        .then(() => {
          this.setState ({
          name: '',
          email: '',
          password: ''})
        })
         
    
}
  
    render() {
      return (
        <div className="sign-into">
          <form onSubmit={this.handleSubmit}>
            <label>
              Name:
              <input type="text" name="name" id="name" value={this.state.name} onChange={this.handleChange} required />
            </label>
            <label>
              Email:
              <input type="text" name="email" id="email" onChange={this.handleChange} />
            </label>
            <label>
              Password:
              <input type="text" name="password" id="password" onChange={this.handleChange} />
            </label>
            <button type="Create User">Add</button>
          </form>

        {/* <div>
          <div class="modal-header">
            <h5 class="modal-title" id="exampleModalLongTitle">Add New User</h5>
            <button type="button" class="close" data-bs-dismiss="modal" aria-label="Close">
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div class="modal-body">
            <form onSubmit={this.handleSubmit}>
              <label>
                Name:
                <input type="text" name="name" id="name" onChange={this.handleChange} />
              </label>
              <label>
                Email:
                <input type="text" name="email" id="email" onChange={this.handleChange} />
              </label>
              <label>
                Password:
                <input type="text" name="password" id="password" onChange={this.handleChange} />
              </label>
              <button type="Create User">Save New User</button>
            </form>
          </div>
          <div class="modal-footer">
            <button type="button" class="btn btn-primary" data-bs-dismiss="modal">Close</button>
          </div> */}

        {/* </div> */}
        </div>
      )

    }
      }
    export default Signupcontainer

