import React from 'react'
import axios from 'axios'


export default class Logincontainer extends React.Component{
    constructor(props){
        super(props);
        this.state={
            email: " ",
            password: " "
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this)
    }
      
      handleChange = (event) => {
      this.setState( { [event.target.name]: event.target.value } )
    }
    handleSubmit(event) {
        const { email,password } = this.state;
   
    
   
   axios.post(`http://localhost:8081/api/users/login`,null,{ params: { userEmail: email, userPassword: password }})
   .then(response => {
    if (response.data.status === "created") {
      this.props.handleSuccessfulAuth(response.data);
    }
  })
  .catch(error => {
    console.log("registration error", error);
  });
        event.preventDefault();
   }
   render() {
            return (
                <div>
                    <form onSubmit={this.handleSubmit}> 
                    <label>
                    <input type="text" name="email"  placeholder="email"  value={this.state.email} onChange={this.handleChange} required />
                    </label>
                    <label>
                        <input type="password"  name="email" placeholder="password"  value={this.state.password} onChange={this.handleChange} required/>
                    </label>
                    <button>
                        Log in
                    </button>
    
                    </form>
                    
                </div>
            )
        }
       

        
   
}



