import React from 'react';
import axios from 'axios';

class SearchByIngredientContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          recipes: [],
          ingredient: ''
        }
    
        this.getRecipes = this.getRecipes.bind(this);
    } 
      
    componentDidMount() {
        this.getRecipes();
    }
      
    getRecipes() {
        axios.get(`http://localhost:8080/recipe-ingredients/get-recipe-names?ingredientName1=` + this.state.ingredient)
          .then(res => {
            const recipes = res.data;
            this.setState({ recipes });
            console.log(res.data);
          })
    }

    handleSubmit(event) {
        this.componentDidMount();
        event.preventDefault();
    }

    handleChange(event) {
        this.setState({ingredient: event.target.ingredient})
    }

    render() {
            const{recipes} = this.state;
            return (
                <div className = "Search"> 
                    <h1>Search By Ingredient</h1>
                    <form onSubmit={this.handleSubmit}>
                        <label htmlFor="header-search">
                            <span className="visually-hidden">Search Ingredient</span>
                        </label>
                    
                        <input
                            type="text"
                            value={this.state.ingredient}
                            onChange={this.handleChange}
                            id="header-search"
                            placeholder="Enter Ingredient"
                            name="s" 
                        />
                    
                        <button type="submit" value="Submit">Search</button>

                    </form>
                </div>
            )


            // return (
            //     <div>
            //        {recipes.map(recipe => <div key={recipes.id}>{recipes}</div>)} 
            //     </div>

            // )
    
            // <form onSubmit={this.handleSubmit}>
            //     <input type='text' value={this.state.value} onChange={this.handleChange} placeholder='Customer Id' />
            //     <input type='submit' value='Submit' />
            // </form> 
        
    }


}

export default SearchByIngredientContainer
