import React from 'react';
import axios from 'axios';
import findrecipes from '../Pages/findrecipes';

export default class FindRecipeByName extends React.Component {
    state = {
        recipes: [],
    };

    componentDidMount() {
        axios.get('http://localhost:8080/recipes/{recipeName}').then(res => {
            console.log(res);
            this.setState({ recipes: res.data });
        });
    }
    render() {
        return (
            <div>
                <ul>
                    {this.state.recipes.map(recipe => <li key = {recipe.id}>{recipe.name}</li>)}
                </ul>
                
            </div>
        )
    }
}
