import { useHistory } from "react-router";
import axios from "axios";
import React from "react";

export default function GetRecipeFromIngredients(props) {

    const [recipes, setRecipes] = React.useState([])
    const [ingredient, bindIngredient, resetIngredient] = React.useState('')

    React.useEffect(() => {
      axios.get('http://localhost:8080/recipe-ingredients/get-recipe-names?ingredientName1=Quinoa').then((response) =>{
          setRecipes(response.data)
      });
    }, []);

    if(!recipes) {
        return null;
    }

    // const handleChange=(e) =>{
    //     this.setState({
    //         [e.target.name]: e.target.value
    //     })
    // }

    const handleSubmit=(e) =>{
        e.preventDefault();
        resetIngredient();
    }


    return (
        <>
            <div className = "Search"> 
                <h1>Search By Ingredient</h1>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="header-search">
                        <span className="visually-hidden">Search Ingredient</span>
                    </label>
                
                    <input
                        type="text"
                        {...bindIngredient}
                        // value={ingredient}
                        // onChange={handleChange}
                        // id="header-search"
                        placeholder="Enter Ingredient"
                        // name="ingredient" 
                    />
                    <button type="submit" value="Submit">Search</button>
                </form>
            </div>

            {recipes.map((recipe, index) => 
                                           <a href="/recipes" ><div key={recipe.id}> {recipe}</div><br></br></a>
                                            )}
        </>
    )
            

}
