import React from 'react';
import axios from 'axios';
import { Route } from 'react-router-dom';

class SearchByIngredientContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          recipes: [],
          ingredient: '',
          currentRecipe: ''
        }
    
        this.getRecipes = this.getRecipes.bind(this);
    } 
      
    componentDidMount() {
        this.getRecipes();
    }
      
    getRecipes() {
        axios.get('http://localhost:8080/recipe-ingredients/get-recipe-names', {params: {ingredientName1: this.state.ingredient} })
          .then(res => {
            const recipes = res.data;
            this.setState({ recipes });
            console.log(res.data);
          })
    }


    handleChange=(e) =>{
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit=(e) =>{
        this.componentDidMount();
        e.preventDefault();
        console.log(this.state)
    }

  

    render() {
            const{recipes, ingredient, currentRecipe} = this.state;
            return (
                <>
                    <div className = "Search"> 
                        <h1>Search By Ingredient</h1>
                        <form onSubmit={this.handleSubmit}>
                            <label htmlFor="header-search">
                                <span className="visually-hidden">Search Ingredient</span>
                            </label>
                        
                            <input
                                type="text"
                                value={ingredient}
                                onChange={this.handleChange}
                                id="header-search"
                                placeholder="Enter Ingredient"
                                name="ingredient" 
                            />
                        
                            <button type="submit" value="Submit">Search</button>

                        </form>
                        <div>
                                {recipes.map(recipe => 
                                <a href="/recipes" ><div key={recipe.id}> {recipe}</div><br></br></a>
                                )} 

                        </div>
                    </div>
                    <Route path = "/recipes">
                        <recipe name = "Hello Recipes" />
                    </Route>  
                   
                </>
            )
    }


}

export default SearchByIngredientContainer
