import { useHistory } from "react-router";
import axios from "axios";
import React, {useState} from "react";

export default function GetRecipeFromIngredients(props) {

    const [recipes, setRecipes] = React.useState([])
    const [state, setState] = React.useState({
        ingredient : ""
    })

    React.useEffect(() => {
      axios.get('http://localhost:8080/recipe-ingredients/get-recipe-names', {params: {ingredientName1: "Quinoa"} }).then((response) =>{
          setRecipes(response.data)
      });
    }, []);

    if(!recipes) {
        return null;
    }

    const handleChange=(e) =>{
        // this.setState({
        //     [e.target.name]: e.target.value
        // })

        const {id, value} = e.target.value
        setState(prevState => ({
            ...prevState,
            [id] : value
        }))
    }

    const handleSubmitClick=(e) => {
        e.preventDefault();
        
    }


    return (
        <>
            <div className = "Search"> 
                <h1>Search By Ingredient</h1>
                <form onSubmit={handleSubmitClick}>
                    <label htmlFor="header-search">
                        <span className="visually-hidden">Search Ingredient</span>
                    </label>
                
                    <input
                        type="text"
                        value={state.ingredient}
                        onChange={handleChange}
                        id="header-search"
                        placeholder="Enter Ingredient"
                        name="ingredient" 
                    />
                    <button type="submit" value="Submit">Search</button>
                </form>
            </div>

            {recipes.map((recipe, index) => 
                                           <a href="/recipes" ><div key={recipe.id}> {recipe}</div><br></br></a>
                                            )}
        </>
    )
            

}
